import Header from './Header';
import { useEffect, useState } from 'react';
import getTreatmentsFromApi from '../services/api-treatments';
import IsUserAuthenticated from '../hooks/IsUserAuthenticated';

const Preview = (props) =>{
    //Treatments
    const auth = IsUserAuthenticated();
    const [treatments, setTreatments] = useState ([]);
    const [doctor, setDoctor] = useState(auth());


    useEffect(() => {
        getTreatmentsFromApi(doctor.doctorId, props.date).then(response => {
          setTreatments(response);
        });
      }, [props.doctorId, props.date, props]);

    const renderTreatments = () => {
        return treatments.map((treatment) => {
            return (
                <section className='section-hour'>
                    <p className="info">{treatment.hour}</p>
                    <p className="info">{treatment.description}</p>
                </section>
            );
        });
    }

    return(
        <>
            <Header/>
            <article className='mainSchedule'>
                <h1 className='title-schedule'>Pr√≥ximas consultas</h1>
                {renderTreatments()}
            </article>
        </>                       
    )
}

export default Preview;
