import Header from './Header';
import { useEffect, useState } from 'react';
import getTreatmentsFromApi from '../services/api-treatments';
import IsUserAuthenticated from '../hooks/IsUserAuthenticated';
import { getCurrentWeek } from '../utils/utils';

const Week=(props)=>{
    const [treatments, setTreatments] = useState ([]);
    const [selectedDate, setSelectedDate] = useState (props.date.getDay());
    const auth = IsUserAuthenticated();
    useEffect(() => {
        const doctor = auth();
        if(doctor) {
            const week = getCurrentWeek();
            const treatments = [];
            for(let day in week){
                getTreatmentsFromApi(doctor.doctorId, week[day]).then(response => {
